int f=10;float v(vec2 v){return fract(sin(dot(v,vec2(12.9898,4.1414)))*43758.5);}float t(vec2 f){vec2 x=floor(f),m=fract(f);m=m*m*(3.-2.*m);float t=mix(mix(v(x),v(x+vec2(1.,0.)),m.x),mix(v(x+vec2(0.,1.)),v(x+vec2(1.,1.)),m.x),m.y);return t*t;}float n(vec2 m){float x=0.,v=.5;vec2 n=vec2(100);mat2 c=mat2(cos(.5),sin(.5),-sin(.5),cos(.5));for(int r=0;r<f;++r)x+=v*t(m),m=c*m*2.+n,v*=.5;return x;}